import React from 'react';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import { Button } from '@material-ui/core';

// TODO 해인아 요기 수정하면대
class SearchBar extends React.Component {

    state = {
        addr: '',
        searchDialog: false
    }

    handleChange = (e) => {
        this.setState({
            addr: e.target.value
        });
    }

    handleClick = () => {
        //this.setState({ searchDialog: true });
        //alert(this.state.addr);

        //주소-좌표 변환 객체를 생성
        let geocoder = new window.daum.maps.services.Geocoder();

        //키워드 검색 객체를 생성
        let ps = new window.daum.maps.services.Places();

        //result는 list로 나오므로, 최상위 값을 일단 출력
        geocoder.addressSearch(this.state.addr, function(result, status){
            if(status === window.daum.maps.services.Status.OK){
                let coords = new window.daum.maps.LatLng(result[0].y, result[0].x);
                //alert(result[0].y, result[0].x);

            }else{
                ps.keywordSearch(this.state.addr, function(result, status){
                    if(status === window.daum.maps.services.Status.OK){
                        let coords = new window.daum.maps.LatLng(result[0].y, result[0].x);
                        //alert(result[0].y, result[0].x);
                    }
                })
            }
        })
        //주소로 좌표를 검색

    }

    handleClose = () => {
        this.setState({ searchDialog: false });
    };



    render() {
        return (
            <div>
                <TextField
                    id="standard-search"
                    label="장소를 검색하세요!"
                    type="search"
                    margin="normal"
                    value={this.state.addr}
                    onChange={this.handleChange}
                />

                <Button
                    style={{backgroundColor: "yellow"}}
                    id="searchBtn"
                    onClick={this.handleClick}
                >검색</Button>

                <Dialog
                    onClose={this.handleClose}
                    aria-labelledby="customized-dialog-title"
                    open={this.state.searchDialog}
                >
                    <TextField
                        id="standard-search2"
                        label="Search field"
                        type="search"
                        margin="normal"
                    />
                </Dialog>
            </div>
        );
    }
}

export default SearchBar;
