import React from 'react';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import { Button } from '@material-ui/core';

// TODO 해인아 요기 수정하면대
class SearchBar extends React.Component {

    state = {
        addr: '',
        searchDialog: false
    }

    handleChange = (e) => {
        this.setState({
            addr: e.target.value
        });
    }

    handleBtnClick = () => {
        this.setState({ searchDialog: true });
        //alert(this.state.addr);

        //검색 input
        let input = this.state.addr;

        //주소-좌표 변환 객체를 생성
        let geocoder = new window.daum.maps.services.Geocoder();

        //키워드 검색 객체를 생성
        let ps = new window.daum.maps.services.Places();

        //주소로 검색한 경우
        geocoder.addressSearch(input, function(result, status, pagination){
            if(input!= null && status === window.daum.maps.services.Status.OK){
                //let coords = new window.daum.maps.LatLng(result[0].y, result[0].x);
                //alert("Lat: "+result[0].y+", \n"+
                //        "Lng: "+result[0].x);

                //검색 목록 출력
                displayPlaces(result);
                //페이지 번호 출력
                displayPagination(pagination);
            }else if(input!=null){ //키워드(장소명)로 검색한 경우
                ps.keywordSearch(input, function(result, status, pagination){
                    if(status === window.daum.maps.services.Status.OK){
                        //let coords = new window.daum.maps.LatLng(result[0].y, result[0].x);
                        //alert("Lat: "+result[0].y+", \n"+
                        //        "Lng: "+result[0].x);

                        //검색 목록 출력
                        displayPlaces(result);
                        //페이지 번호 출력
                        displayPagination(pagination);
                    }else if (status === window.daum.maps.services.Status.ZERO_RESULT) {
                        alert('검색 결과가 존재하지 않습니다.');
                        return;       
                    } else if (status === window.daum.maps.services.Status.ERROR) {        
                        alert('검색 결과 중 오류가 발생했습니다.');
                        return;
                    }
                })
            }else if (status === window.daum.maps.services.Status.ZERO_RESULT) {
                alert('검색 결과가 존재하지 않습니다.');
                return;       
            } else if (status === window.daum.maps.services.Status.ERROR) {        
                alert('검색 결과 중 오류가 발생했습니다.');
                return;
            }
        });

        //검색 결과 목록을 출력하는 함수
        function displayPlaces(places) {
            let listEl = document.getElementById('placeList'),
            menuEl = document.getElementById('dialog'),
            fragment = document.createDocumentFragment(),
            //bounds = new window.daum.maps.LatLngBounds(),
            listStr = '';

            //검색 결과 목록에 추가된 항목들을 제거
            //removeAllChildNodes(listEl);

            for(let i=0;i<places.length;i++){
                //검색 결과 항목 Element를 생성
                let itemEl = getListItem(i, places[i]);
                fragment.appendChild(itemEl);
            } 

            //검색 결과 항목들을 검색 결과 목록 Element에 추가
            listEl.appendChild(fragment);
            menuEl.scrollTop = 0;
        }

        //검색 결과 항목을 Element로 반환하는 함수
        function getListItem(index, places){
            let el = document.createElement('li'),
            itemStr = '<span class="markerbg marker_' + (index+1) + '"></span>' +
                '<div class="info">' +
                '   <h5>' + places.place_name + '</h5>';

            if(places.road_address_name) {
                itemStr += '    <span>' + places.road_address_name + '</span>' +
                    '   <span class="jibun gray">' +  places.address_name  + '</span>';
            } else{
                itemStr += '    <span>' +  places.address_name  + '</span>'; 
            }

            itemStr += '  <span class="tel">' + places.phone  + '</span>' +
                '</div>';

            el.innerHTML = itemStr;
            el.className = 'item';

            return el;
        }

        //검색 결과 목록 하단에 페이지 번호를 표시하는 함수
        function displayPagination(pagination) {
            let paginationEl = document.getElementById('pagination'),
            fragment = document.createDocumentFragment(),
            i;

            //기존에 추가된 페이지 번호 삭제
            while(paginationEl.hasChildNodes()) {
                paginationEl.removeChild(paginationEl.lastChild);
            }

            for(i=1;i<=pagination.last;i++){
                let el = document.createElement('a');
                el.href = "#";
                el.innerHTML = i;

                if(i===pagination.current){
                    el.className = 'on';
                }else{
                    el.onClick = (function(i){
                        return function() {
                            pagination.gotoPage(i);
                        }
                    })(i);
                }
                fragment.appendChild(el);
            }
            paginationEl.appendChild(fragment);
        }

        //검색 결과 목록의 자식 Element를 제거하는 함수
        function removeAllChildNodes(el) {
            while(el.hasChildNodes()){
                el.removeChild(el.lastChild);
            }
        }
    }

    handleClose = () => {
        this.setState({ searchDialog: false });
    };

    render() {
        return (
            <div>
                <TextField
                    id="standard-search"
                    label="장소를 검색하세요!"
                    type="search"
                    margin="normal"
                    value={this.state.addr}
                    onChange={this.handleChange}
                />

                <Button
                    style={{backgroundColor: "yellow"}}
                    id="searchBtn"
                    onClick={this.handleBtnClick}
                >검색</Button>

                <Dialog
                    id= "dialog"
                    onClose={this.handleClose}
                    aria-labelledby="customized-dialog-title"
                    open={this.state.searchDialog}
                >
                    <ul id="placesList"></ul>
                    <div id="pagination"></div>
                </Dialog>
            </div>
        );
    }
}

export default SearchBar;
