import React from 'react';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import { Button, AppBar, Toolbar, List, ListItem, ListItemText } from '@material-ui/core';
import IconButton from '@material-ui/core/IconButton';
import Typography from '@material-ui/core/Typography';
import CloseIcon from '@material-ui/icons/Close';
import { isTemplateElement } from '@babel/types';

// TODO 해인아 요기 수정하면대
class SearchBar extends React.Component {

    state = {
        addr: '',
        searchDialog: false,
        results: []
    }

    handleChange = (e) => {
        this.setState({
            addr: e.target.value
        });
    }

    handleBtnClick = () => {
        this.setState({ searchDialog: true });
        //this.forceUpdate();
        //alert(this.state.addr);

        //검색 input
        let input = this.state.addr;

        //주소-좌표 변환 객체를 생성
        let geocoder = new window.daum.maps.services.Geocoder();

        //키워드 검색 객체를 생성
        let ps = new window.daum.maps.services.Places();

        let searchResult = [];

        //주소로 검색한 경우
        geocoder.addressSearch(input, function(result, status, pagination){
            if(input!= null && status === window.daum.maps.services.Status.OK){
                //let coords = new window.daum.maps.LatLng(result[0].y, result[0].x);
                //alert("Lat: "+result[0].y+", \n"+
                //        "Lng: "+result[0].x);

                //alert(result[0].place_name+"\n" //장소명
                //    +result[0].road_address_name+"\n"   //도로명주소
                //    +result[0].address_name+"\n"    //지번주소
                //    +result[0].phone);  //전화번호 //있으면 출력 없으면 안 출력

                searchResult = result;

                //검색 목록 출력
                //displayPlaces(result);
                //페이지 번호 출력
                //displayPagination(pagination);
            }else if(input!=null){ //키워드(장소명)로 검색한 경우
                ps.keywordSearch(input, function(result, status, pagination){
                    if(status === window.daum.maps.services.Status.OK){
                        //let coords = new window.daum.maps.LatLng(result[0].y, result[0].x);
                        //alert("Lat: "+result[0].y+", \n"+
                        //        "Lng: "+result[0].x);

                        searchResult = result;

                        //검색 목록 출력
                        //displayPlaces(result);
                        //페이지 번호 출력
                        //displayPagination(pagination);
                    }else if (status === window.daum.maps.services.Status.ZERO_RESULT) {
                        alert('검색 결과가 존재하지 않습니다.');
                        return;       
                    } else if (status === window.daum.maps.services.Status.ERROR) {        
                        alert('검색 결과 중 오류가 발생했습니다.');
                        return;
                    }
                })
            }else if (status === window.daum.maps.services.Status.ZERO_RESULT) {
                alert('검색 결과가 존재하지 않습니다.');
                return;       
            } else if (status === window.daum.maps.services.Status.ERROR) {        
                alert('검색 결과 중 오류가 발생했습니다.');
                return;
            }
        });

        this.setState({results: searchResult});

    }

    handleClose = () => {
        this.setState({ searchDialog: false });
    };

    render() {
        return (
            <div>
                <TextField
                    id="standard-search"
                    label="장소를 검색하세요!"
                    type="search"
                    margin="normal"
                    value={this.state.addr}
                    onChange={this.handleChange}
                />

                <Button
                    style={{backgroundColor: "yellow"}}
                    id="searchBtn"
                    onClick={this.handleBtnClick}
                >검색</Button>

                <Dialog
                    id= "dialog"
                    onClose={this.handleClose}
                    aria-labelledby="customized-dialog-title"
                    open={this.state.searchDialog}>
                        <AppBar position="relative">
                            <Toolbar>
                            <Typography variant="h6" marginLeft="theme.spacing(2)" flex={1}>
                                장소 검색
                            </Typography>
                            <IconButton edge="end" color="inherit" onClick={this.handleClose} aria-label="Close">
                                <CloseIcon />
                            </IconButton>
                            </Toolbar>
                            </AppBar>
                            {/*<List>
                                <ListItem button>
                                    <ListItemText primary={result.place_name} secondary="haein" />
                                </ListItem>
                            </List>
                            */}
                            <List>
                                {this.state.results.map(result => 
                                    <ListItem>
                                        <ListItemText primary={result.place_name}/>
                                    </ListItem>
                                    )}
                            </List>

                </Dialog>
            </div>
        );
    }
}

export default SearchBar;
